sys_script_client.script:
function onSubmit(sysIDs, table, oldValues, newValue, callback) {
    var saveAndClose = true;
    //Type appropriate comment here, and begin script below
    if (isLoading || newValue == '') {
        return;
    }

    var ga = new GlideAjax('x_845657_operate.verifyEmail');
    ga.addParam('sysparm_name', 'isEmail');
    ga.addParam('sysparm_userEmail', g_form.getValue("u_email"));
    ga.getXMLAnswer(getEmail);


    function getEmail(answer) {
        var clearvalue; // Stays Undefined
        if (answer) {
            var returneddata = JSON.parse(answer);
            g_form.addErrorMessage();
        } else {
            g_form.setValue("u_email", clearvalue);
        }


        callback(saveAndClose);
    }
}
